// Basic initialization to prevent errors
window.tinymce = window.tinymce || {};
window.tinymce.dom = window.tinymce.dom || {};
window.tinymce.util = window.tinymce.util || {};
window.tinymce.html = window.tinymce.html || {};
window.tinymce.ui = window.tinymce.ui || {};

// Create a minimal editor implementation
window.tinymce.Editor = function (id, settings) {
    this.id = id;
    this.settings = settings || {};
    this.initialized = false;

    this.init = function () {
        this.initialized = true;
        if (typeof settings.setup === "function") {
            settings.setup(this);
        }
        this.fire("init");
        return this;
    };

    this.fire = function (eventName) {
        const handlers = this._eventHandlers && this._eventHandlers[eventName];
        if (handlers) {
            handlers.forEach((handler) => handler.call(this));
        }
        return this;
    };

    this.on = function (eventName, callback) {
        this._eventHandlers = this._eventHandlers || {};
        this._eventHandlers[eventName] = this._eventHandlers[eventName] || [];
        this._eventHandlers[eventName].push(callback);
        return this;
    };

    this.getContent = function () {
        return this._content || "";
    };

    this.setContent = function (content) {
        this._content = content;
        return this;
    };
};

// Create a minimal init function
window.tinymce.init = function (settings) {
    const editor = new window.tinymce.Editor(settings.selector, settings);
    editor.init();
    window.tinymce.activeEditor = editor;
    return editor;
};

// Create a notification manager that does nothing
window.tinymce.NotificationManager = function () {
    this.open = function () {};
    this.close = function () {};
    this.getNotifications = function () {
        return [];
    };
};

// Ensure the activeEditor has a notification manager
window.tinymce.activeEditor = window.tinymce.activeEditor || {
    notificationManager: new window.tinymce.NotificationManager(),
};
